mapper.insert:
INSERT INTO EVENT (EVENT_NO
				  , EVENT_TITLE
				  , EVENT_START
				  , EVENT_DEW
				  , EVENT_IMG_NAME
				  , EVENT_CONTENT)
	VALUES (SEQ_EVENT_NO.NEXTVAL
			, #{evtTitle}
			, #{evtStart}
			, #{evtDew}
			, #{evtImgName}
			, #{evtContent})

mapper.update:
UPDATE EVENT
SET EVENT_STATUS = 'N'
WHERE EVENT_NO = #{eno}

UPDATE EVENT
   SET EVENT_TITLE = #{evtTitle}
     , EVENT_START = #{evtStart}
     , EVENT_DEW = #{evtDew}
     , EVENT_IMG_NAME = #{evtImgName}
     , EVENT_CONTENT = #{evtContent}
 WHERE EVENT_NO = #{evtNo}

mapper.select:
SELECT EVENT_NO
     , MEMBER_NO
FROM EVENT_LIKE
WHERE EVENT_NO = #{evtNo}
  AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE
FROM EVENT_LIKE
WHERE EVENT_NO = #{evtNo}
  AND MEMBER_NO = #{memberNo}

mapper.select:
SELECT COUNT(*) COUNT
FROM EVENT_LIKE
WHERE EVENT_NO = #{evtNo}

mapper.insert:
INSERT INTO EVENT_REPLY (REPLY_NO
                       , REPLY_CONTENT
                       , PARENT_REPLY
                       , EVENT_NO
                       , MEMBER_NO)
				 VALUES (SEQ_REPLY_NO.NEXTVAL
				       , #{replyContent}
				       , #{parentReply}
				       , #{evtNo}
				       , #{memberNo})
