mapper.insert:
INSERT INTO MAGAZINE (MAGAZINE_NO
					, CATEGORY_NO
				    , MAGAZINE_TITLE
				    , MAGAZINE_CONTENT
				    , MAGAZINE_IMG_NAME
				    , MAGAZINE_HASHTAG)
			  VALUES (SEQ_MAGAZINE_NO.NEXTVAL
					, #{categoryNo}
					, #{magazineTitle}
					, #{magazineContent}
					, #{magazineImgName}
					, #{magazineHashtag})

mapper.update:
UPDATE MAGAZINE
   SET MAGAZINE_STATUS = 'N'
 WHERE MAGAZINE_NO = #{magazineNo}

UPDATE MAGAZINE
   SET CATEGORY_NO = #{categoryNo}
     , MAGAZINE_TITLE = #{magazineTitle}
     , MAGAZINE_CONTENT = #{magazineContent}
     , MAGAZINE_IMG_NAME = #{magazineImgName}
     , MAGAZINE_HASHTAG = #{magazineHashtag}
 WHERE MAGAZINE_NO = #{magazineNo}

mapper.select:
	SELECT MAGAZINE_NO
	     , MEMBER_NO
	  FROM MAGAZINE_LIKE
     WHERE MAGAZINE_NO = #{magazineNo}
	   AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE
  FROM MAGAZINE_LIKE
 WHERE MAGAZINE_NO = #{magazineNo}
   AND MEMBER_NO = #{memberNo}

mapper.select:
SELECT COUNT(*)
  FROM MAGAZINE_LIKE
 WHERE MAGAZINE_NO = #{magazineNo}

mapper.update:
UPDATE MAGAZINE
   SET MAGAZINE_COUNT = MAGAZINE_COUNT + 1
 WHERE MAGAZINE_NO = #{magazineNo}
