mapper.insert:
INSERT INTO ORDERS (ORDER_NO
				  , ORDER_DATE
				  , ORDER_PRICE
				  , MEMBER_NO
				  , SUBS_COUNT
				  , CUSTOMER_UID
				  , ORDER_RECEIPT
				  , ORDER_STATUS
				  , SUBS_STATUS
				  , ADDRESS)
			VALUES (#{orderNo}
				  , TO_DATE(#{orderDate}, 'YYYYMMDDHH24MISS')
				  , #{orderPrice}
				  , #{memberNo}
				  , #{subsCount}
				  , #{customerUid}
				  , #{orderReceipt}
				  , #{orderStatus}
				  , #{subsStatus}
				  , #{address})

mapper.update:
UPDATE ORDERS
   SET SUBS_STATUS = 'C'
     , CUSTOMER_UID = SUBSTR(#{customerUid}, -4) || '_' || #{orderNo}
 WHERE CUSTOMER_UID = #{customerUid}
   AND SUBS_STATUS = 'Y'
   AND ORDER_STATUS = 'Y'

mapper.select:
SELECT NVL(MAX(CART_AMOUNT), 0) "CART_AMOUNT"
  FROM CART
 WHERE MEMBER_NO = #{memberNo}
   AND PRODUCT_NO = #{productNo}
   AND CART_STATUS = 'Y'

mapper.update:
UPDATE CART
   SET CART_AMOUNT = #{cartAmount}
 WHERE MEMBER_NO = #{memberNo}
   AND PRODUCT_NO = #{productNo}
   AND CART_STATUS = 'Y'

mapper.delete:
DELETE CART
 WHERE PRODUCT_NO = #{productNo}
   AND MEMBER_NO = #{memberNo}
   AND CART_STATUS = 'Y'

mapper.select:
SELECT COUNT(*)
FROM ORDERS
WHERE MEMBER_NO = #{memberNo}

SELECT COUNT(DISTINCT CUSTOMER_UID)
FROM ORDERS
WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE ORDERS
   SET DELIVERY = #{delivery}
   	 , ADDRESS = #{address}
 WHERE ORDER_NO = #{orderNo}
