mapper.update:
UPDATE MEMBER
   SET MEMBER_BIRTH_YEAR = #{memberBirthYear}
     , HEIGHT = #{height}
     , WEIGHT = #{weight}
     , GENDER = #{gender}
 WHERE MEMBER_NO = #{memberNo}

UPDATE POLL
   SET POLL_STATUS = 'N'
 WHERE POLL_NO = ${pollNo}

mapper.delete:
DELETE CART
 WHERE MEMBER_NO = #{memberNo}
   AND CART_STATUS = 'Y'
