mapper.insert:
INSERT INTO REVIEW (REVIEW_NO
				  , REVIEW_TITLE
				  , REVIEW_CONTENT
				  , REVIEW_COUNT
				  , REVIEW_GRADE
				  , MEMBER_NO
				  , ORDER_NO)
			VALUES (SEQ_REVIEW_NO.NEXTVAL
				  , #{reviewTitle}
				  , #{reviewContent}
				  , #{reviewCount}
				  , #{reviewGrade}
				  , #{memberNo}
				  , #{orderNo})

INSERT INTO REVIEW_FILE (REVIEW_FILE_NO
				  , CHANGE_NAME
				  , FILE_PATH
				  , FILE_LEVEL
				  , REVIEW_NO)
			VALUES (SEQ_REVIEW_FILE_NO.NEXTVAL
				  , #{changeName}
				  , #{filePath}
				  , #{fileLevel}
				  , #{reviewNo})

mapper.update:
   		 UPDATE REVIEW
		   SET REVIEW_STATUS = 'N'
		 WHERE REVIEW_NO = #{rno2}

    	UPDATE REVIEW_FILE
		   SET FILE_STATUS = 'N'
		 WHERE REVIEW_NO = #{rno2}

UPDATE REVIEW_REPLY
   SET REPLY_STATUS = 'N'
 WHERE REPLY_NO = #{replyNo}
